import React, { useState, useEffect } from "react";
import axios from "axios"; // Import Axios

const limitPerPage = 20;
const apiUrl =
  "https://api.themoviedb.org/3/discover/movie?api_key=42792e702bdfaa79e4dcffe831c6aa8b&language=en-US";

const getUsers = async (page = 1) => {
  const actualUrl = `${apiUrl}&page=${page}&limit=${limitPerPage}`;
  const response = await fetch(actualUrl);
  return response.json();
};

const getEntireUserList = async (page = 1) => {
  const result = await getUsers(page);
  console.log("Retrieving data from API for page:", page);
  console.log(result);
  if (result.length > 0) {
    return result.concat(getEntireUserList(page + 1));
  } else {
    return result;
  }
};

useEffect(() => {
  async function fetchData() {
    const entireList = await getEntireUserList();
    console.log(entireList);
    setMovies(entireList.results);
    setLoading(false);
  }
  fetchData();
}, [setMovies]);

export default axios;
