import { useState } from "react";
import axios from "axios";

export default function Search() {
  const [movies, setMovies] = useState([]);
  const [query, setQuery] = useState(""); // Add state for search query

  // Create a function to fetch movies based on the query
  const fetchMovies = async () => {
    try {
      const apiKey = "42792e702bdfaa79e4dcffe831c6aa8b"; // Replace with your actual API key
      const baseUrl = `https://api.themoviedb.org/3/search/movie`;
      const language = "en-US";
      const response = await axios.get(
        `${baseUrl}?api_key=${apiKey}&language=${language}&query=${query}`
      );
      setMovies(response.data.results);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  // Use the useEffect hook to fetch movies when the query changes
  useEffect(() => {
    if (query.trim() !== "") {
      fetchMovies();
    } else {
      // Clear movies when the query is empty
      setMovies([]);
    }
  }, [query]);

  return (
    <SearchBar>
      <Icon />
      <SearchInput
        query={query}
        onQueryChange={(newQuery) => setQuery(newQuery)}
      />
      <NumResult movies={movies} />
    </SearchBar>
  );
}

function SearchBar({ children }) {
  return <nav className="search-bar">{children}</nav>;
}
function Icon() {
  return (
    <div>
      <h1>You just need..</h1>
      <span>üçø</span>
    </div>
  );
}
function SearchInput({ query, onQueryChange }) {
  return (
    <input
      className="search"
      type="text"
      placeholder="Search movies.."
      value={query}
      onChange={(e) => onQueryChange(e.target.value)}
    />
  );
}
function NumResult({ movies }) {
  return (
    <p className="num-results">
      Found <strong>{movies.length}</strong>results
    </p>
  );
}
